internal class HtmlInlineRenderer : WinUIObjectRenderer<HtmlInline>
{
    protected override void Write(WinUIRenderer renderer, HtmlInline obj)
    {
        if (renderer == null)
        {
            throw new ArgumentNullException(nameof(renderer));
        }

